package LoopStatement;

public class WhileLoopExamples {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int count=0;
		while(count<5) {
			System.out.println(count);
			count++;
		}
		
		count =5;
		while(count<5) {
			System.out.println(count);
			count++;
		}

	}

}
